mapper.select:
        SELECT


--         级联表的字段

        FROM zhaopinxinxi  a



                and a.id in

                #{item}



                and a.zhaopinxinxi_name like CONCAT('%',#{params.zhaopinxinxiName},'%')


                and a.zhaopinxinxi_types = #{params.zhaopinxinxiTypes}


                  and a.zhaopinxinxi_number >= #{params.zhaopinxinxiNumberStart}   


                  and a.zhaopinxinxi_number <= #{params.zhaopinxinxiNumberEnd}   


                and a.zhaopinxinxi_number = #{params.zhaopinxinxiNumber}


                and a.zhaopinxinxi_content like CONCAT('%',#{params.zhaopinxinxiContent},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
